INSERT INTO `t_project` VALUES (1,'1','连云港港赣榆港区（一期）区域建设用海。',NULL,NULL,1,'通过围填海形成陆域，形成港口、物流堆场、工业等功能用地。','1'),(2,'2','连云港港徐圩港区液体散货泊位区区域用海',NULL,NULL,2,'重点建设徐圩港区液体散货泊位区配套服务设施、连云港石化产业公共液体化工储罐、徐圩港区液体散货作业区储罐等项目','1'),(3,'3','连云港港徐圩港区临港产业区区域用海',NULL,NULL,3,'建设徐圩港区化肥物流仓储基地、徐圩港区物流公共信息中心、徐圩港区临港产业服务区2#预留通用堆场、徐圩港区临港产业服务区配套服务设施（主中心）等项目','1'),(4,'4','埒子口-灌河口临港综合开发区域建设围填海',NULL,NULL,4,'连云港港灌河港区燕尾港作业区码头三期、四期工程及江苏天生港港口有限公司森柏粮油物流园及配套设施项目工程、散杂货堆场，集装箱物流园区、建材堆场、矿石堆场等围填工程','1'),(5,'5','滨海港区远景预留发展区建设用海',NULL,NULL,5,'主要服务于远期滨海港后续的大型临港产业开发建设需要','1'),(6,'6','大丰区大丰港临港工业区区域建设用海',NULL,NULL,6,'以发展沿海临港工业和特色加工业为主，建设成为长江三角洲北翼临港产业集聚基地，形成江苏省中部沿海地区的现代港口综合物流基地','1'),(7,'7','大丰区新王竹围垦养殖区域农业围垦用海',NULL,NULL,7,'主要用于海水无公害养殖，将本规划海域建成高标准的水产品质量安全示范养殖区','1'),(8,'8','条子泥二期工程项目',NULL,NULL,8,'发展高效农（渔）业养殖和水产品加工以及绿色能源产业','1'),(9,'9','弶港一级渔港围垦项目',NULL,NULL,9,'加快东台市沿海开发，调整渔业产业结构，拉动沿海经济发展','1'),(10,'10','老坝港滨海新区三期工程',NULL,NULL,10,'重点建设医院、新城、学校等一批公共服务项目以及一批工业、旅游业等项目','1'),(11,'11','通州湾渔业集聚区万亩海蜇园',NULL,NULL,11,'建设万亩海蜇园项目','1'),(12,'12','通州湾临港区域建设围填海',NULL,NULL,12,'重点建设通州湾航空产业园、集装箱物流园区、华东商贸国际物流园、通州湾海铁物流中心及件杂货物流园区等项目','1'),(13,'13','通州湾东凌渔港工程',NULL,NULL,13,'按照传统渔港保障需求和休闲港区两大功能区建造东凌渔港，打造科研院所的中试孵区、海洋生物制药高新区、城镇商业地产综合体等','1'),(14,'14','生态海湾暨三夹沙二期工程',NULL,NULL,14,'初步完成陆域匡围及防潮大堤建设，可配套闸口观光带、人工沙滩区、亲水娱乐区等项目规划建设海滨公园区、商务休闲度假区、高端居住生活区、自然生态区、湿地公园区、高端别墅区，创建高品质滨海生态旅游度假胜地','1'),(15,'15','东元湾中国江海产业经济园南区',NULL,NULL,15,'重点开发战略性新兴产业、加工制造业、装备制造业和生产性服务业等产业','1'),(16,'16','东海滩涂围垦高效养殖示范区围垦项目',NULL,NULL,16,'通过填海造地将滩涂资源转化为农用地，缓解农业用地日益减少的局面。通过完善基础设施，采取工程措施改良土壤，将围区建成优质的生态农业区','1');
